<body>
    <div class="app">
        <header>
            <a href="/workout" class="back-link">‚Üê Back to Workout</a>
            <h1>ü§ñ Workout Debrief</h1>
            <div id="date-display"></div>
            <div id="model-display" class="model-badge"></div>
        </header>

        <div class="chat-container">
            <div class="messages" id="messages">
                <div class="message system">Loading...</div>
            </div>
            <div class="input-area">
                <input type="text" id="chat-input" placeholder="Share your thoughts..." disabled>
                <button class="send-btn" id="send-btn" disabled onclick="sendMessage()">‚û§</button>
            </div>
        </div>
    </div>

    <script>
        const urlParams = new URLSearchParams(window.location.search);
        const today = urlParams.get('date') || new Date().toISOString().split('T')[0];
        const displayDate = new Date(today + 'T12:00:00');
        document.getElementById('date-display').textContent = displayDate.toLocaleDateString('en-US', {
            weekday: 'long', month: 'short', day: 'numeric'
        });

        let conversationId = null;
        const messagesDiv = document.getElementById('messages');
        const chatInput = document.getElementById('chat-input');
        const sendBtn = document.getElementById('send-btn');

        async function init() {
            try {
                const resp = await fetch(`/api/debrief/init?date=${today}`);
                const data = await resp.json();
                conversationId = data.conversation_id;

                // Display model info
                if (data.model) {
                    document.getElementById('model-display').textContent = data.model;
                }

                messagesDiv.innerHTML = '';
                for (let i = 0; i < data.messages.length; i++) {
                    const msg = data.messages[i];
                    const isLast = i === data.messages.length - 1;
                    addMessage(msg.role, msg.content, msg.tool_calls, msg.tool_results, isLast);
                }

                chatInput.disabled = false;
                sendBtn.disabled = false;
                chatInput.focus();

                scrollToBottom();
            } catch (err) {
                messagesDiv.innerHTML = `<div class="message system">Error: ${err.message}</div>`;
            }
        }

        // Configure marked for safe rendering
        marked.setOptions({
            breaks: true,
            gfm: true
        });

        function renderMarkdown(text) {
            return marked.parse(text);
        }

        function addMessage(role, content, toolCalls, toolResults, showRetry = false) {
            const div = document.createElement('div');
            div.className = `message ${role}`;

            const contentSpan = document.createElement('div');
            contentSpan.className = 'message-content';
            // Render markdown for assistant messages, plain text for user
            if (role === 'assistant' || role === 'system') {
                contentSpan.innerHTML = renderMarkdown(content);
            } else {
                contentSpan.textContent = content;
            }
            div.appendChild(contentSpan);

            if (toolCalls && toolCalls.length > 0) {
                const toolDiv = document.createElement('div');
                toolDiv.className = 'tool-container';
                for (const tc of toolCalls) {
                    const badge = document.createElement('span');
                    // Different styling based on tool type
                    const isReadOnly = tc.name === 'get_workout_history';
                    badge.className = `tool-badge ${isReadOnly ? 'info' : 'success'}`;
                    badge.innerHTML = `<span class="tool-icon">${isReadOnly ? 'üîç' : '‚úì'}</span> ${formatToolName(tc.name)}`;
                    toolDiv.appendChild(badge);
                }
                div.appendChild(toolDiv);
            }

            if (showRetry && role === 'assistant') {
                const actionsDiv = document.createElement('div');
                actionsDiv.className = 'message-actions';
                actionsDiv.id = 'retry-actions';
                const retryBtn = document.createElement('button');
                retryBtn.className = 'retry-btn';
                retryBtn.innerHTML = '<span class="icon">‚Üª</span> Retry';
                retryBtn.onclick = retryLastMessage;
                actionsDiv.appendChild(retryBtn);
                div.appendChild(actionsDiv);
            }

            messagesDiv.appendChild(div);
            return div;
        }

        function createStreamingMessage() {
            const div = document.createElement('div');
            div.className = 'message assistant';
            div.id = 'streaming-message';

            const contentSpan = document.createElement('div');
            contentSpan.className = 'message-content';
            contentSpan.innerHTML = '<span class="streaming-cursor"></span>';
            div.appendChild(contentSpan);

            messagesDiv.appendChild(div);
            return div;
        }

        function updateStreamingMessage(text) {
            const div = document.getElementById('streaming-message');
            if (!div) return;
            const contentSpan = div.querySelector('.message-content');
            contentSpan.innerHTML = renderMarkdown(text) + '<span class="streaming-cursor"></span>';
            scrollToBottom();
        }

        function finalizeStreamingMessage(text, toolCalls, toolResults) {
            const div = document.getElementById('streaming-message');
            if (!div) return;
            div.id = ''; // Remove the streaming ID

            const contentSpan = div.querySelector('.message-content');
            contentSpan.innerHTML = renderMarkdown(text);

            if (toolCalls && toolCalls.length > 0) {
                const toolDiv = document.createElement('div');
                toolDiv.className = 'tool-container';
                for (const tc of toolCalls) {
                    const badge = document.createElement('span');
                    const isReadOnly = tc.name === 'get_workout_history';
                    badge.className = `tool-badge ${isReadOnly ? 'info' : 'success'}`;
                    badge.innerHTML = `<span class="tool-icon">${isReadOnly ? 'üîç' : '‚úì'}</span> ${formatToolName(tc.name)}`;
                    toolDiv.appendChild(badge);
                }
                div.appendChild(toolDiv);
            }

            // Add retry button
            const actionsDiv = document.createElement('div');
            actionsDiv.className = 'message-actions';
            actionsDiv.id = 'retry-actions';
            const retryBtn = document.createElement('button');
            retryBtn.className = 'retry-btn';
            retryBtn.innerHTML = '<span class="icon">‚Üª</span> Retry';
            retryBtn.onclick = retryLastMessage;
            actionsDiv.appendChild(retryBtn);
            div.appendChild(actionsDiv);
        }

        function formatToolName(name) {
            const names = {
                'adjust_exercise': 'Adjusted exercise',
                'update_weekly_progression': 'Updated progression',
                'get_workout_history': 'Looked up history'
            };
            return names[name] || name;
        }

        function addTypingIndicator() {
            const div = document.createElement('div');
            div.className = 'message assistant';
            div.id = 'typing';
            div.innerHTML = '<div class="typing-indicator"><span></span><span></span><span></span></div>';
            messagesDiv.appendChild(div);
            scrollToBottom();
        }

        function removeTypingIndicator() {
            const el = document.getElementById('typing');
            if (el) el.remove();
        }

        function scrollToBottom() {
            window.scrollTo(0, document.body.scrollHeight);
        }

        function removeRetryButton() {
            const existing = document.getElementById('retry-actions');
            if (existing) existing.remove();
        }

        async function retryLastMessage() {
            if (!conversationId) return;

            // Disable input while retrying
            chatInput.disabled = true;
            sendBtn.disabled = true;

            // Remove the last assistant message from UI
            const messages = messagesDiv.querySelectorAll('.message.assistant');
            if (messages.length > 0) {
                messages[messages.length - 1].remove();
            }

            // Show typing indicator
            addTypingIndicator();
            scrollToBottom();

            try {
                const resp = await fetch('/api/debrief/retry', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({
                        conversation_id: conversationId
                    })
                });

                removeTypingIndicator();

                if (!resp.ok) {
                    const errData = await resp.json().catch(() => ({ error: 'Failed to get response' }));
                    throw new Error(errData.error || 'Failed to get response');
                }

                const data = await resp.json();
                addMessage('assistant', data.message, data.tool_calls, data.tool_results, true);

                if (data.tool_results && data.tool_results.length > 0) {
                    for (const tr of data.tool_results) {
                        addMessage('system', `Applied: ${tr.result}`);
                    }
                }

            } catch (err) {
                removeTypingIndicator();
                addMessage('system', `Error: ${err.message}`);
            }

            chatInput.disabled = false;
            sendBtn.disabled = false;
            chatInput.focus();
            scrollToBottom();
        }

        async function sendMessage() {
            const message = chatInput.value.trim();
            if (!message || !conversationId) return;

            chatInput.value = '';
            chatInput.disabled = true;
            sendBtn.disabled = true;

            // Remove retry button from previous message
            removeRetryButton();
            addMessage('user', message);
            scrollToBottom();

            // Create streaming message placeholder
            createStreamingMessage();
            scrollToBottom();

            let fullText = '';
            let toolCalls = [];
            let toolResults = [];

            try {
                const resp = await fetch('/api/debrief/chat/stream', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({
                        conversation_id: conversationId,
                        message: message
                    })
                });

                if (!resp.ok) {
                    throw new Error('Failed to get response');
                }

                const reader = resp.body.getReader();
                const decoder = new TextDecoder();
                let buffer = '';

                while (true) {
                    const { done, value } = await reader.read();
                    if (done) break;

                    buffer += decoder.decode(value, { stream: true });
                    const lines = buffer.split('\n');
                    buffer = lines.pop(); // Keep incomplete line in buffer

                    for (const line of lines) {
                        if (line.startsWith('event: ')) {
                            const eventType = line.slice(7);
                            continue;
                        }
                        if (line.startsWith('data: ')) {
                            const data = line.slice(6);
                            try {
                                const parsed = JSON.parse(data);
                                if (parsed.text !== undefined) {
                                    fullText += parsed.text;
                                    updateStreamingMessage(fullText);
                                } else if (parsed.tool_calls !== undefined) {
                                    toolCalls = parsed.tool_calls || [];
                                    toolResults = parsed.tool_results || [];
                                } else if (parsed.name) {
                                    // Tool use event
                                    toolCalls.push(parsed);
                                } else if (parsed.tool_id) {
                                    // Tool result event
                                    toolResults.push(parsed);
                                }
                            } catch (e) {
                                // Ignore parse errors
                            }
                        }
                    }
                }

                finalizeStreamingMessage(fullText, toolCalls, toolResults);

                if (toolResults && toolResults.length > 0) {
                    for (const tr of toolResults) {
                        addMessage('system', `Applied: ${tr.result}`);
                    }
                }

            } catch (err) {
                // Remove streaming message on error
                const streamingMsg = document.getElementById('streaming-message');
                if (streamingMsg) streamingMsg.remove();
                addMessage('system', `Error: ${err.message}`);
            }

            chatInput.disabled = false;
            sendBtn.disabled = false;
            chatInput.focus();
            scrollToBottom();
        }

        chatInput.addEventListener('keypress', (e) => {
            if (e.key === 'Enter' && !e.shiftKey) {
                e.preventDefault();
                sendMessage();
            }
        });

        init();
    </script>
</body>
