func (s *Server) executeToolCalls(ctx context.Context, toolCalls []map[string]interface{}) []map[string]interface{} {
	q := dbgen.New(s.DB)
	results := make([]map[string]interface{}, 0, len(toolCalls))

	for _, tc := range toolCalls {
		name := tc["name"].(string)
		input := tc["input"].(map[string]interface{})
		var result string

		switch name {
		case "adjust_exercise":
			exerciseID := int64(input["exercise_id"].(float64))
			changes := []string{}

			if sets, ok := input["sets"]; ok {
				q.UpdateExerciseSets(ctx, dbgen.UpdateExerciseSetsParams{
					Sets: int64(sets.(float64)),
					ID:   exerciseID,
				})
				changes = append(changes, fmt.Sprintf("sets to %v", sets))
			}
			if reps, ok := input["reps"]; ok {
				r := int64(reps.(float64))
				q.UpdateExerciseReps(ctx, dbgen.UpdateExerciseRepsParams{
					Reps: &r,
					ID:   exerciseID,
				})
				changes = append(changes, fmt.Sprintf("reps to %v", reps))
			}
			if dur, ok := input["duration_seconds"]; ok {
				d := int64(dur.(float64))
				q.UpdateExerciseDuration(ctx, dbgen.UpdateExerciseDurationParams{
					DurationSeconds: &d,
					ID:              exerciseID,
				})
				changes = append(changes, fmt.Sprintf("duration to %vs", dur))
			}
			if load, ok := input["target_load"]; ok {
				l := load.(string)
				q.UpdateExerciseTargetLoad(ctx, dbgen.UpdateExerciseTargetLoadParams{
					TargetLoad: &l,
					ID:         exerciseID,
				})
				changes = append(changes, fmt.Sprintf("target load to %s", load))
			}

			if len(changes) > 0 {
				result = fmt.Sprintf("Updated exercise %d: %v", exerciseID, changes)
			} else {
				result = "No changes specified"
			}

		case "update_weekly_progression":
			weekStart := int64(input["week_start"].(float64))
			changes := []string{}

			if rdl, ok := input["rdl_target"]; ok {
				r := rdl.(string)
				q.UpdateProgressionRDL(ctx, dbgen.UpdateProgressionRDLParams{
					RdlTarget: &r,
					WeekStart: weekStart,
				})
				changes = append(changes, fmt.Sprintf("RDL target to %s", rdl))
			}
			if height, ok := input["step_down_height"]; ok {
				h := height.(string)
				q.UpdateProgressionStepDown(ctx, dbgen.UpdateProgressionStepDownParams{
					StepDownHeight: &h,
					WeekStart:      weekStart,
				})
				changes = append(changes, fmt.Sprintf("step-down height to %s", height))
			}

			if len(changes) > 0 {
				result = fmt.Sprintf("Updated week %d progression: %v", weekStart, changes)
			} else {
				result = "No changes specified"
			}

		case "get_workout_history":
			startDate := input["start_date"].(string)
			endDate := input["end_date"].(string)

			var historyJSON []byte
			if exerciseName, ok := input["exercise_name"]; ok && exerciseName != "" {
				exName := exerciseName.(string)
				history, err := q.GetWorkoutHistoryByDateRangeAndExercise(ctx, dbgen.GetWorkoutHistoryByDateRangeAndExerciseParams{
					Date:    startDate,
					Date_2:  endDate,
					Column3: &exName,
				})
				if err != nil {
					result = fmt.Sprintf("Error fetching history: %v", err)
				} else {
					historyJSON, _ = json.Marshal(history)
					result = string(historyJSON)
				}
			} else {
				history, err := q.GetWorkoutHistoryByDateRange(ctx, dbgen.GetWorkoutHistoryByDateRangeParams{
					Date:   startDate,
					Date_2: endDate,
				})
				if err != nil {
					result = fmt.Sprintf("Error fetching history: %v", err)
				} else {
					historyJSON, _ = json.Marshal(history)
					result = string(historyJSON)
				}
			}

		default:
			result = "Unknown tool"
		}

		results = append(results, map[string]interface{}{
			"tool_id": tc["id"],
			"result":  result,
		})
	}

	return results
}

// HandleDebriefChatStream handles streaming chat responses via SSE
func (s *Server) HandleDebriefChatStream(w http.ResponseWriter, r *http.Request) {
	ctx := r.Context()
	q := dbgen.New(s.DB)

	var req DebriefChatRequest
	if err := json.NewDecoder(r.Body).Decode(&req); err != nil {
		http.Error(w, err.Error(), http.StatusBadRequest)
		return
	}

	// Save user message
	_, err := q.AddDebriefMessage(ctx, dbgen.AddDebriefMessageParams{
		ConversationID: req.ConversationID,
		Role:           "user",
		Content:        req.Message,
	})
	if err != nil {
		http.Error(w, err.Error(), http.StatusInternalServerError)
		return
	}

	// Get conversation history
	msgs, err := q.GetDebriefMessages(ctx, req.ConversationID)
	if err != nil {
		http.Error(w, err.Error(), http.StatusInternalServerError)
		return
	}

	// Get context data
	exercises, _ := q.GetAllExercises(ctx)
	progressions, _ := q.GetAllProgressions(ctx)
	recentLogs, _ := q.GetRecentLogs(ctx, 30)

	exercisesJSON, _ := json.Marshal(exercises)
	progressionsJSON, _ := json.Marshal(progressions)
	logsJSON, _ := json.Marshal(recentLogs)

	planContext, _ := os.ReadFile("/home/exedev/itbs-tracker/plan_context.md")

	systemPrompt := fmt.Sprintf(`You are a supportive fitness coach helping someone recover from ITBS. You're having a debrief conversation after their workout.

Plan context:
%s

Current exercises:
%s

Weekly progressions:
%s

Recent workout logs (last 30 entries):
%s

You have access to tools:
- adjust_exercise: Modify exercise parameters when user agrees to changes
- update_weekly_progression: Update progression targets
- get_workout_history: Look up detailed workout history by date range. Use this when the user asks about past workouts, trends, or wants to compare sessions.

Always confirm before making changes. Be concise and supportive.`, string(planContext), string(exercisesJSON), string(progressionsJSON), string(logsJSON))

	messages := s.buildMessagesFromHistory(msgs)

	// Set up SSE
	w.Header().Set("Content-Type", "text/event-stream")
	w.Header().Set("Cache-Control", "no-cache")
	w.Header().Set("Connection", "keep-alive")

	flusher, ok := w.(http.Flusher)
	if !ok {
		http.Error(w, "Streaming not supported", http.StatusInternalServerError)
		return
	}

	// Stream the response with tool loop support
	fullResponse, allToolCalls, allToolResults, err := s.streamClaudeWithToolLoop(ctx, w, flusher, systemPrompt, messages)
	if err != nil {
		slog.Error("streaming error", "error", err)
		fmt.Fprintf(w, "event: error\ndata: %s\n\n", err.Error())
		flusher.Flush()
		return
	}

	// Save assistant message
	var toolCallsJSON, toolResultsJSON *string
	if len(allToolCalls) > 0 {
		tc, _ := json.Marshal(allToolCalls)
		s := string(tc)
		toolCallsJSON = &s
	}
	if len(allToolResults) > 0 {
		tr, _ := json.Marshal(allToolResults)
		s := string(tr)
		toolResultsJSON = &s
	}

	_, err = q.AddDebriefMessage(ctx, dbgen.AddDebriefMessageParams{
		ConversationID: req.ConversationID,
		Role:           "assistant",
		Content:        fullResponse,
		ToolCalls:      toolCallsJSON,
		ToolResults:    toolResultsJSON,
	})
	if err != nil {
		slog.Warn("failed to save assistant message", "error", err)
	}

	// Send done event with tool info
	doneData := map[string]interface{}{
		"tool_calls":   allToolCalls,
		"tool_results": allToolResults,
	}
	doneJSON, _ := json.Marshal(doneData)
	fmt.Fprintf(w, "event: done\ndata: %s\n\n", string(doneJSON))
	flusher.Flush()
}

func (s *Server) streamClaudeWithToolLoop(ctx context.Context, w http.ResponseWriter, flusher http.Flusher, systemPrompt string, messages []map[string]interface{}) (string, []map[string]interface{}, []map[string]interface{}, error) {
	var allToolCalls []map[string]interface{}
	var allToolResults []map[string]interface{}
	var finalResponse string

	// Loop to handle tool use cycles (max 3 iterations)
	for i := 0; i < 3; i++ {
		response, toolCalls, stopReason, err := s.streamClaudeAPI(ctx, w, flusher, systemPrompt, messages)
		if err != nil {
			return "", nil, nil, err
		}

		finalResponse = response

		if len(toolCalls) == 0 || stopReason == "end_turn" {
			return finalResponse, allToolCalls, allToolResults, nil
		}

		// Execute tool calls
		toolResults := s.executeToolCalls(ctx, toolCalls)
		allToolCalls = append(allToolCalls, toolCalls...)
		allToolResults = append(allToolResults, toolResults...)

		// Send tool events
		for _, tc := range toolCalls {
			toolEvent, _ := json.Marshal(tc)
			fmt.Fprintf(w, "event: tool_use\ndata: %s\n\n", string(toolEvent))
			flusher.Flush()
		}
		for _, tr := range toolResults {
			resultEvent, _ := json.Marshal(tr)
			fmt.Fprintf(w, "event: tool_result\ndata: %s\n\n", string(resultEvent))
			flusher.Flush()
		}

		// Build assistant message with tool use
		assistantContent := []map[string]interface{}{}
		if response != "" {
			assistantContent = append(assistantContent, map[string]interface{}{
				"type": "text",
				"text": response,
			})
		}
		for _, tc := range toolCalls {
			assistantContent = append(assistantContent, map[string]interface{}{
				"type":  "tool_use",
				"id":    tc["id"],
				"name":  tc["name"],
				"input": tc["input"],
			})
		}
		messages = append(messages, map[string]interface{}{
			"role":    "assistant",
			"content": assistantContent,
		})

		// Build tool result message
		toolResultContent := []map[string]interface{}{}
		for _, tr := range toolResults {
			toolResultContent = append(toolResultContent, map[string]interface{}{
				"type":        "tool_result",
				"tool_use_id": tr["tool_id"],
				"content":     tr["result"],
			})
		}
		messages = append(messages, map[string]interface{}{
			"role":    "user",
			"content": toolResultContent,
		})
	}

	return "I'm having trouble processing that request.", allToolCalls, allToolResults, nil
}

func (s *Server) streamClaudeAPI(ctx context.Context, w http.ResponseWriter, flusher http.Flusher, systemPrompt string, messages []map[string]interface{}) (string, []map[string]interface{}, string, error) {
	reqBody := map[string]interface{}{
		"model":      claudeModel,
		"max_tokens": 1000,
		"system":     systemPrompt,
		"messages":   messages,
		"tools":      debriefTools,
		"stream":     true,
	}

	jsonBody, _ := json.Marshal(reqBody)
	req, _ := http.NewRequestWithContext(ctx, "POST", "https://api.anthropic.com/v1/messages", bytes.NewReader(jsonBody))
	req.Header.Set("Content-Type", "application/json")
	req.Header.Set("x-api-key", s.AnthropicKey)
	req.Header.Set("anthropic-version", "2023-06-01")

	client := &http.Client{Timeout: 120 * time.Second}
	resp, err := client.Do(req)
	if err != nil {
		return "", nil, "", err
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK {
		body, _ := io.ReadAll(resp.Body)
		return "", nil, "", fmt.Errorf("API error: %s", string(body))
	}

	var fullText strings.Builder
	var toolCalls []map[string]interface{}
	var currentToolID, currentToolName string
	var currentToolInput strings.Builder
	var stopReason string

	scanner := bufio.NewScanner(resp.Body)
	// Increase buffer size for large responses
	buf := make([]byte, 0, 64*1024)
	scanner.Buffer(buf, 1024*1024)

	for scanner.Scan() {
		line := scanner.Text()
		if !strings.HasPrefix(line, "data: ") {
			continue
		}
		data := strings.TrimPrefix(line, "data: ")
		if data == "" {
			continue
		}

		var event struct {
			Type         string `json:"type"`
			Index        int    `json:"index"`
			ContentBlock *struct {
				Type  string `json:"type"`
				ID    string `json:"id"`
				Name  string `json:"name"`
				Input interface{} `json:"input"`
			} `json:"content_block"`
			Delta *struct {
				Type        string `json:"type"`
				Text        string `json:"text"`
				PartialJSON string `json:"partial_json"`
				StopReason  string `json:"stop_reason"`
			} `json:"delta"`
			Message *struct {
				StopReason string `json:"stop_reason"`
			} `json:"message"`
		}

		if err := json.Unmarshal([]byte(data), &event); err != nil {
			continue
		}

		switch event.Type {
		case "content_block_start":
			if event.ContentBlock != nil && event.ContentBlock.Type == "tool_use" {
				currentToolID = event.ContentBlock.ID
				currentToolName = event.ContentBlock.Name
				currentToolInput.Reset()
			}

		case "content_block_delta":
			if event.Delta != nil {
				if event.Delta.Type == "text_delta" && event.Delta.Text != "" {
					fullText.WriteString(event.Delta.Text)
					// Send text chunk to client
					chunk := map[string]string{"text": event.Delta.Text}
					chunkJSON, _ := json.Marshal(chunk)
					fmt.Fprintf(w, "event: text\ndata: %s\n\n", string(chunkJSON))
					flusher.Flush()
				} else if event.Delta.Type == "input_json_delta" && event.Delta.PartialJSON != "" {
					currentToolInput.WriteString(event.Delta.PartialJSON)
				}
			}

		case "content_block_stop":
			if currentToolID != "" {
				var input map[string]interface{}
				json.Unmarshal([]byte(currentToolInput.String()), &input)
				toolCalls = append(toolCalls, map[string]interface{}{
					"id":    currentToolID,
					"name":  currentToolName,
					"input": input,
				})
				currentToolID = ""
				currentToolName = ""
			}

		case "message_delta":
			if event.Delta != nil && event.Delta.StopReason != "" {
				stopReason = event.Delta.StopReason
			}

		case "message_stop":
			// Done
		}
	}

	return fullText.String(), toolCalls, stopReason, nil
}
