func (s *Server) HandleDebriefInit(w http.ResponseWriter, r *http.Request) {
	ctx := r.Context()
	q := dbgen.New(s.DB)

	dateStr := r.URL.Query().Get("date")
	if dateStr == "" {
		dateStr = todayInNY()
	}

	// Get or create conversation
	conv, err := q.GetDebriefConversation(ctx, dateStr)
	needInitialMessage := false
	if err == sql.ErrNoRows {
		// Create new conversation
		convID, err := q.CreateDebriefConversation(ctx, dateStr)
		if err != nil {
			http.Error(w, err.Error(), http.StatusInternalServerError)
			return
		}
		conv = dbgen.DebriefConversation{ID: convID, Date: dateStr}
		needInitialMessage = true
	} else if err != nil {
		http.Error(w, err.Error(), http.StatusInternalServerError)
		return
	}

	// Get all messages
	msgs, err := q.GetDebriefMessages(ctx, conv.ID)
	if err != nil {
		http.Error(w, err.Error(), http.StatusInternalServerError)
		return
	}

	// If conversation exists but has no messages, generate initial message
	if len(msgs) == 0 || needInitialMessage {
		initialMsg, err := s.generateInitialDebrief(ctx, dateStr)
		if err != nil {
			slog.Error("failed to generate initial debrief", "error", err)
			initialMsg = "I had trouble loading your workout data. How did your workout go today?"
		}

		// Save initial message
		msgID, err := q.AddDebriefMessage(ctx, dbgen.AddDebriefMessageParams{
			ConversationID: conv.ID,
			Role:           "assistant",
			Content:        initialMsg,
		})
		if err != nil {
			slog.Warn("failed to save initial message", "error", err)
		} else {
			// Add the message to our list
			msgs = []dbgen.DebriefMessage{{
				ID:             msgID,
				ConversationID: conv.ID,
				Role:           "assistant",
				Content:        initialMsg,
			}}
		}
	}

	// Get workout summary
	summary, _ := q.GetWorkoutSummary(ctx, dateStr)

	// Convert messages
	respMsgs := make([]DebriefMessageResp, len(msgs))
	for i, m := range msgs {
		respMsgs[i] = DebriefMessageResp{
			Role:    m.Role,
			Content: m.Content,
		}
		if m.ToolCalls != nil {
			json.Unmarshal([]byte(*m.ToolCalls), &respMsgs[i].ToolCalls)
		}
		if m.ToolResults != nil {
			json.Unmarshal([]byte(*m.ToolResults), &respMsgs[i].ToolResults)
		}
	}

	w.Header().Set("Content-Type", "application/json")
	json.NewEncoder(w).Encode(DebriefInitResponse{
		ConversationID: conv.ID,
		Messages:       respMsgs,
		WorkoutSummary: summary,
		Model:          claudeModel,
	})
}

func (s *Server) generateInitialDebrief(ctx context.Context, date string) (string, error) {
	q := dbgen.New(s.DB)

	// Get today's workout summary
	summary, err := q.GetWorkoutSummary(ctx, date)
	if err != nil {
		return "", err
	}

	// Get schedule info
	schedule, _ := q.GetScheduleForDate(ctx, date)

	// Get recent history for context
	history, _ := q.GetRecentWorkoutHistory(ctx, date)

	// Get exercises
	exercises, _ := q.GetAllExercises(ctx)

	// Get progression
	progression, _ := q.GetProgressionForWeek(ctx, dbgen.GetProgressionForWeekParams{
		WeekStart: schedule.WeekNumber,
		WeekEnd:   schedule.WeekNumber,
	})

	// Build context
	summaryJSON, _ := json.Marshal(summary)
	historyJSON, _ := json.Marshal(history)
	exercisesJSON, _ := json.Marshal(exercises)
	progressionJSON, _ := json.Marshal(progression)

	planContext, _ := os.ReadFile("/home/exedev/itbs-tracker/plan_context.md")

	prompt := fmt.Sprintf(`You are a supportive fitness coach helping someone recover from ITBS (IT Band Syndrome). They just completed a workout and you're doing a brief debrief.

Plan context:
%s

Current week: %d
Workout type: %s

Today's workout summary:
%s

Recent workout history (last 2 weeks):
%s

All exercises in the plan:
%s

Current week's progression targets:
%s

Provide a brief, encouraging summary of their workout. Mention:
1. What they accomplished today
2. Any notable performance (good or areas to watch)
3. How this fits into their overall progress

Keep it concise and conversational (2-3 short paragraphs). End by asking if they have any thoughts about the workout or if anything felt off that they'd like to discuss.`,
		string(planContext),
		schedule.WeekNumber,
		schedule.WorkoutType,
		string(summaryJSON),
		string(historyJSON),
		string(exercisesJSON),
		string(progressionJSON),
	)

	return s.callClaudeSimple(ctx, prompt)
}

func (s *Server) HandleDebriefRetry(w http.ResponseWriter, r *http.Request) {
	ctx := r.Context()
	q := dbgen.New(s.DB)

	var req DebriefRetryRequest
	if err := json.NewDecoder(r.Body).Decode(&req); err != nil {
		http.Error(w, err.Error(), http.StatusBadRequest)
		return
	}

	// Delete the last assistant message
	err := q.DeleteLastAssistantMessage(ctx, req.ConversationID)
	if err != nil {
		slog.Warn("failed to delete last assistant message", "error", err)
		// Continue anyway - we'll regenerate
	}

	// Get conversation history (now without the last assistant message)
	msgs, err := q.GetDebriefMessages(ctx, req.ConversationID)
	if err != nil {
		http.Error(w, err.Error(), http.StatusInternalServerError)
		return
	}

	// If there are no messages or no user messages, regenerate initial debrief
	hasUserMessage := false
	for _, m := range msgs {
		if m.Role == "user" {
			hasUserMessage = true
			break
		}
	}

	var convDate string
	row := s.DB.QueryRowContext(ctx, "SELECT date FROM debrief_conversations WHERE id = ?", req.ConversationID)
	row.Scan(&convDate)

	if !hasUserMessage {
		// Regenerate initial debrief
		initialMsg, err := s.generateInitialDebrief(ctx, convDate)
		if err != nil {
			slog.Error("failed to generate initial debrief", "error", err)
			w.Header().Set("Content-Type", "application/json")
			w.WriteHeader(http.StatusInternalServerError)
			json.NewEncoder(w).Encode(map[string]string{"error": "Failed to generate response: " + err.Error()})
			return
		}

		// Save initial message
		_, err = q.AddDebriefMessage(ctx, dbgen.AddDebriefMessageParams{
			ConversationID: req.ConversationID,
			Role:           "assistant",
			Content:        initialMsg,
		})
		if err != nil {
			slog.Warn("failed to save assistant message", "error", err)
		}

		w.Header().Set("Content-Type", "application/json")
		json.NewEncoder(w).Encode(DebriefChatResponse{
			Message: initialMsg,
		})
		return
	}

	// Get context data for regenerating response
	exercises, _ := q.GetAllExercises(ctx)
	progressions, _ := q.GetAllProgressions(ctx)
	recentLogs, _ := q.GetRecentLogs(ctx, 30)

	exercisesJSON, _ := json.Marshal(exercises)
	progressionsJSON, _ := json.Marshal(progressions)
	logsJSON, _ := json.Marshal(recentLogs)

	planContext, _ := os.ReadFile("/home/exedev/itbs-tracker/plan_context.md")

	// Call Claude with tools
	response, allToolCalls, allToolResults, err := s.callClaudeWithToolLoop(ctx, msgs, string(planContext), string(exercisesJSON), string(progressionsJSON), string(logsJSON))
	if err != nil {
		slog.Error("claude error", "error", err)
		w.Header().Set("Content-Type", "application/json")
		w.WriteHeader(http.StatusInternalServerError)
		json.NewEncoder(w).Encode(map[string]string{"error": "AI error: " + err.Error()})
		return
	}

	// Save assistant message
	var toolCallsJSON, toolResultsJSON *string
	if len(allToolCalls) > 0 {
		tc, _ := json.Marshal(allToolCalls)
		s := string(tc)
		toolCallsJSON = &s
	}
	if len(allToolResults) > 0 {
		tr, _ := json.Marshal(allToolResults)
		s := string(tr)
		toolResultsJSON = &s
	}

	_, err = q.AddDebriefMessage(ctx, dbgen.AddDebriefMessageParams{
		ConversationID: req.ConversationID,
		Role:           "assistant",
		Content:        response,
		ToolCalls:      toolCallsJSON,
		ToolResults:    toolResultsJSON,
	})
	if err != nil {
		slog.Warn("failed to save assistant message", "error", err)
	}

	w.Header().Set("Content-Type", "application/json")
	json.NewEncoder(w).Encode(DebriefChatResponse{
		Message:     response,
		ToolCalls:   allToolCalls,
		ToolResults: allToolResults,
	})
}

